mapper.update:
UPDATE BI_REPORT_FOLDER
CREATE_USER_ID = #{createUserId,jdbcType=BIGINT}, ALIAS = #{alias,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE BI_REPORT_FOLDER
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM BI_REPORT_FOLDER
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM BI_REPORT_FOLDER


		AND ID = #{id,jdbcType=INTEGER}


		AND CREATE_USER_ID = #{createUserId,jdbcType=BIGINT}


		AND ALIAS = #{alias,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM BI_REPORT_FOLDER


		AND ID = #{id,jdbcType=INTEGER}


		AND CREATE_USER_ID = #{createUserId,jdbcType=BIGINT}


		AND ALIAS = #{alias,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
